import './App.css';
import Header from './components/Header/Header';
import PostList from './components/PostList/PostList';
import MySelect from "./components/UI/Select/MySelect";
import PostForm from './components/PostForm/PostForm';
import { useMemo, useState } from 'react';
import MyInput from './components/UI/Input/MyInput';

function App() {
  const [posts, setPosts] = useState ([
      {id: 1, title: "JavaScript", body: "description"},
      {id: 2, title: "PHP", body: "description"},
      {id: 3, title: "Node.js", body: "description"},
  ])

  const [selectedSort, setSelectedSort] = useState('');
  const [seachQuery, setSeachQuery] = useState('');

  const sortedPost = useMemo(() => {
        console.log('Функція відпрацювала');
        if(selectedSort) {
          return [...posts].sort((a,b) => a[selectedSort].localeCompare(b[selectedSort]))
        } else {
          return posts;
        }

  }, [selectedSort, posts]);

  const sortedAndSeachesPosts = useMemo(() => {
    return sortedPost.filter(post => post.title.toLowerCase().includes(seachQuery.toLowerCase()))
  }, [seachQuery, sortedPost])

const createPost = (newPost) => {
  setPosts([...posts, newPost])
}

const removePost = (post) => {
    setPosts(posts.filter(p => p.id !== post.id))
}

const sortPosts = (sort) => {
  console.log(sort);
      setSelectedSort(sort);
}

  return (
    <div className="App">
          <Header title="Create Post Page" className="header"></Header>
          <div className='container'>
            <PostForm create={createPost}></PostForm>
            <hr style={{margin: "15px 0 30px 0"}}/>
            <div style={{display: 'flex', gap: "15px", alignItems: 'center'}}>
              <MyInput 
                placeholder="Пошук..."
                value={seachQuery}
                onChange={e => setSeachQuery(e.target.value)}></MyInput>
              <MySelect
                value={selectedSort}
                onChange={sortPosts}
                defaultValue="Сортувати"
                options={[
                  {value: "title", name: "по назві"},
                  {value: "body", name: "по опису"}
                ]} >
              </MySelect>
            </div>

            {sortedAndSeachesPosts.length !== 0 
              ? <PostList remove={removePost} posts={sortedAndSeachesPosts} title="Список постів"></PostList>
              : <div className='info__message'>Постів не знайдено!</div>}

          </div>
     </div>
  );
}

export default App;
